body{

}
.grid-container {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
  padding: 0px 0px;
  grid-row-gap: 10px;
  padding-bottom: 10px;
}

.grid-container-2 {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
  grid-row-gap: 10px;
  padding-top: 10px;
  padding-bottom: 10px;
}

.grid-item {
   font-size: 16px;
   padding: 0px 0px;

}

.dotted-underline {
    border-bottom: 1px dotted #ccc;
    text-align:center;
    font-weight: bold;
}

p {
   font-size: 16px;
}
